import React, { Fragment, useState } from "react";
import { HiSearch } from "react-icons/hi";
import "../Styles/NavBarStyle.css";
import { Routes, Route, NavLink } from "react-router-dom";
import Movies from "./Movies";

import { Nav, NavDropdown } from "react-bootstrap";

export const Container = React.createContext();


const genre = [
    ''
]

function Navbar() {
  const [toggle, setToggle] = useState(true);
  return (
    <Container.Provider value={toggle}>
      <Fragment>
        <nav className="navBarColor nav-bar">
          <div className="nav-options">
            <h1 id="heading">7Flix</h1>
            <NavLink to="">
              <span id="MoviesLight">Movies</span>
            </NavLink>
            <NavDropdown
              id="nav-dropdown-dark-example"
              title="KATEGORI"
              menuVariant="dark"
            >
              <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">
                Another action
              </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
            </NavDropdown>
          </div>
          <div className="input-grouped">
            <input type="text" placeholder="Search movies"></input>
            <HiSearch fontSize={21} color="grey" id="search" />
          </div>
        </nav>
        <Routes>
          <Route path="" element={<Movies />} />
        </Routes>
      </Fragment>
    </Container.Provider>
  );
}

export default Navbar;
